import './App.css';
import { useState } from 'react';
import Header from './Components/Header';

function App() {
  const [state , setState] = useState({
    showHeader:true
  });
  function shoHandler(){
    setState({ showHeader: !state.showHeader })
  }
  return (
    <div className="App">
      <Header newTitle = "abc" />
      <button onClick = { shoHandler} >{state.showHeader ? 'Hide' :'Show'} </button>
    </div>
  );
}

export default App;
